<?php require_once 'conn.php'; try { $sql = "DESCRIBE item_details"; $stmt = $conn->prepare($sql); $stmt->execute(); $columns = $stmt->fetchAll(PDO::FETCH_ASSOC); header('Content-Type: application/json'); echo json_encode(['status' => 'success', 'columns' => $columns], JSON_PRETTY_PRINT); } catch (PDOException $e) { echo json_encode(['status' => 'error', 'message' => 'Database error: ' . $e->getMessage()]); } ?> 
